angular.module("universes",["ngRoute"]),angular.module("universes").config(["$routeProvider",function(e){e.when("/",{controller:"MainController",templateUrl:"templates/not_found.html"}).otherwise({controller:"NotFoundController",templateUrl:"templates/not_found.html"})}]),angular.module("universes").controller("MainController",["$scope","$http",function(e,o){o.get("/api/races").success(function(o){console.log("received races:"),console.log(o),e.races=o}),e.createRace=function(n,r){var t={raceType:n,earType:r};o.post("/api/races",t).success(function(o){e.raceType=e.earType="",e.races.unshift(o)}).error(function(e){console.log(e)})},e.removeRace=function(n){o["delete"]("/api/races/"+n).success(function(){_.remove(e.races,function(e){return e._id===n})})}}]);