angular.module("universes",["ngResource","ngRoute"]),angular.module("universes").config(["$routeProvider",function(e){e.when("/",{controller:"MainController",templateUrl:"templates/races.html"}).otherwise({controller:"NotFoundController",templateUrl:"templates/not_found.html"})}]),angular.module("universes").controller("MainController",["$scope","$http",function(e,o){o.get("/api/races").success(function(o){e.races=o}),e.createRace=function(r,n){var t={raceType:r,earType:n};o.post("/api/races",t).success(function(o){e.raceType=e.earType="",e.races.unshift(o)}).error(function(e){console.log(e)})},e.removeRace=function(r){o["delete"]("/api/races/"+r).success(function(){_.remove(e.races,function(e){return e._id===r})})}}]),angular.module("universes").controller("NotFoundController",["$scope",function(e){e.message="Not found!"}]);